import { Navigate, NavLink } from "react-router-dom"
import { useModal } from "../../hooks/ModalContext"
import { useToken } from "../../hooks/TokenContext"
import Login from '../Login/Login'
import './Header.css'

function Header() {
    const [token, setToken] = useToken()
    const [, setModal] = useModal()
    const handleClick = async e => {
      e.preventDefault()
      setModal(<Login/>)
    /*<div id="buttons">

        {!token && <button id="login-butt" onClick={()=>{<Login/>}}>Login </button>}
        {token && <button id="logout-butt" onClick={()=>setToken('')}>Logout </button>}--!
        <button id="register-butt">Register </button>
      </div>*/
  return (
    <header id="top-bar">
      <div>
        <NavLink to="/">Inicio</NavLink>
        <NavLink to="/service/list">Lista de tareas</NavLink>
        {token && <NavLink to="/service/:id/user">Nueva tarea</NavLink>}
        {token && <NavLink to="/service/add">Crear Servicio</NavLink>}
      </div>
      <div id="buttons">

        {!token && <button id="login-butt" onClick={handleClick}>Login </button>}
        {token && <button id="logout-butt" onClick={()=>setToken('')}>Logout </button>}
        <button id="register-butt">Register </button>
      </div>

    </header>
  )
}
}
export default Header
