import { useState } from "react"
import { Navigate } from "react-router-dom"
import { useToken } from "../hooks/TokenContext"
import { useUser } from "../hooks/UserContext"

function Register() {
    const [user, setUser] = useUser('')
    const [password, setPassword] = useState('')
    const [mail, setMail] = useState('')
    const [token, setToken] = useToken()

    const handleSubmit = async e => {
        e.preventDefault()
        try {
          const res = await fetch('http://127.0.0.1:3000/register', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ user, password, mail })

          })
          const data = await res.json()
          console.log(data)
          if (!res.ok) throw new Error(res.statusText)

          setToken(data.data)
          console.log(data.data)

        } catch (e) {
          console.warn(e)
          console.log(token)
        }
      }


    return (
      <div className="Login">
        <h2>Registro</h2>
        <form onSubmit={handleSubmit}>
          <input placeholder="Username..." value={user} onChange={e=>setUser(e.target.value)}/>
          <input placeholder="Password..." name="password" type="password" value={password} onChange={e => setPassword(e.target.value)}/>
          <input placeholder="Email..." name="email" type="email" value={mail} onChange={e => setMail(e.target.value)}/>
          <button>Registrar</button>
        </form>
      </div>
    )
  }

  export default Register
