import { useState } from "react"
import { Navigate } from "react-router-dom"
import { useModal } from "../../hooks/ModalContext"
import { useToken } from "../../hooks/TokenContext"
import { useUser } from "../../hooks/UserContext"
import Login from "../Login/Login"

function Register() {
    const [username, setUser] = useUser()
    const [password, setPassword] = useState('')
    const [mail, setMail] = useState('')
    const [token, setToken] = useToken()
    const [, setModal] = useModal()
    const handleSubmit = async e => {
        e.preventDefault()
        try {
          const res = await fetch('http://127.0.0.1:3000/register', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ username, password, mail })

          })
          if (!res.ok) throw new Error(res.statusText)
          const data = await res.json()
          setToken(data.data)
        } catch (e) {
          console.warn(e)
        }
      }

      if(token){
        return <Navigate to="/"/>
      }
    return (
      <div className="Login">
        <h2>Registro</h2>
        <form onSubmit={handleSubmit}>
          <input placeholder="Username..." value={username} onChange={e=>setUser(e.target.value)}/>
          <input placeholder="Password..." name="password" type="password" value={password} onChange={e => setPassword(e.target.value)}/>
          <input placeholder="Email..." name="email" type="email" value={mail} onChange={e => setMail(e.target.value)}/>
          <button>Registrar</button>
          <p>No account? <span id="register-link"onClick={()=>setModal(<Login/>)}>Login</span></p>
        </form>
      </div>
    )
  }

  export default Register
